import React, { useState, useEffect } from 'react';
import { getAllUsers, getFollowers } from '../services/userService';
import './UserList.css';

const UserList = ({ user }) => {
    const [users, setUsers] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');

    const userId = user.ID;

    useEffect(() => {
        getAllUsers().then(response => {
            setUsers(response.data);
        }, err => {
            console.log(err);
        });
    }, []);

    useEffect(() => {
        getFollowers(userId).then(response => {
            const followerIds = response.data.followers;
            setUsers(prevUsers => prevUsers.map(user => ({
                ...user,
                isFollowing: followerIds.includes(user.ID)
            })));
        }, err => {
            console.log(err);
        });
    }, []);

    const handleSearchChange = (event) => {
        setSearchQuery(event.target.value);
    };

    const filteredUsers = users.filter(user => {
        const regex = new RegExp(searchQuery, 'i');
        return regex.test(user.Username);
    });

    return (
        <div className="userlist-container">
            <input
                type="text"
                placeholder="Search by username..."
                value={searchQuery}
                onChange={handleSearchChange}
                className="userlist-searchbar"
            />

            {filteredUsers.map(user => (
                <div key={user.ID} className="user-card">
                    <h3>{user.Username}</h3> <button type='button' className='btn btn-primary'>
                        {user.isFollowing ? 'Unfollow' : 'Follow me'}
                    </button>
                    {/* <p>{user.ID}</p> */}

                </div>
            ))}
        </div>
    );
};

export default UserList;
